//import { } from react;
import styled from "styled-components";
import { Button, ButtonGroup } from 'react-bootstrap';

import useInput  from './useInput';



//todo wrapper
const TodoWrapper = styled.div`
  display: flex;
  justify-content: space-around;
  max-width: 645px;
  background: #f6f4e6;
  margin: 10% auto;
  box-shadow: 1.8px 2.4px 5px 0 rgba(0, 0, 0, 0.3);
  padding: 10px 10px;
  color: #776d8a;
  border-radius: 3%;
`
const Todolist = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 600px;
`
const TodoItems = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  margin: 20px 0;
`
// todo headers
const TodoTitle = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 32px;
`
const TodoTitleNails = styled.div`
  margin: 10px;
  width:20px;
  height:20px;
  border: #dbe3e5 solid;
  border-radius: 100%;
  background-color: #dbe3e5;
  box-shadow: 1.8px 2.4px 5px 0 rgba(0, 0, 0, 0.3);
`
const TodoInput = styled.input`
  display: block;
  margin-top: 30px;
  margin-bottom: 35px;
  align-self: center;
  text-align: center;
  padding: 10px 10px;
  width: 100%;
  opacity: 100%;
  background: #f6f4e6;
  color: #776d8a;
  border: none;
  border-radius: 5px;
  transition: all 0.3s;
  outline: none;
  font-size: 20px;
  :hover {
    background: #e8ded2;
    opacity: 50%;
  }
  :focus {
    opacity: 100%;
    color: #776d8a;
    background: #e8ded2;
  }
  ::placeholder {
    color: #776d8a;
    opacity: 50%;
  }
`

export default function TodoUI() {
    const {
      todos, 
      updateFilter,
      handleInputChange,
      handleEnterKeyPress,
      handleDeleteButton,
      handleCheckBox,
      handleClearTodo,
      handleFilterMapping
    } = useInput()

  return (
    <TodoWrapper>
      <Todolist>
        <TodoTitle>
          <TodoTitleNails />
          Todolist
          <TodoTitleNails />
        </TodoTitle>
        <TodoInput type="text" placeholder="add something todo here!" onChange={handleInputChange} onKeyPress={handleEnterKeyPress} />
        <ButtonGroup>
          <Button variant="outline-secondary" onClick={()=>handleFilterMapping("All")}>All</Button>
          <Button variant="outline-secondary" onClick={()=>handleFilterMapping("Done")}>Done</Button>
          <Button variant="outline-secondary" onClick={()=>handleFilterMapping("Undone")}>Undone</Button>
          <Button variant="outline-danger" onClick={handleClearTodo}>Clear All</Button>
        </ButtonGroup>
        <TodoItems>
          {
            todos.map((todo)=>(
              <TodoItem
                key={todo.id}
                todo={todo}
                handleDeleteButton={handleDeleteButton}
                handleCheckBox={handleCheckBox}
              />          
            ))
          }
        </TodoItems>
      </Todolist>
    </TodoWrapper>
  )
}
