import TodoUI from './TodoUI'
import styled from 'styled-components';
import { Button, ButtonGroup } from 'react-bootstrap';


//todo wrapper
const TodoWrapper = styled.div`
  display: flex;
  justify-content: space-around;
  max-width: 645px;
  background: #f6f4e6;
  margin: 10% auto;
  box-shadow: 1.8px 2.4px 5px 0 rgba(0, 0, 0, 0.3);
  padding: 10px 10px;
  color: #776d8a;
  border-radius: 3%;
`
const Todolist = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 600px;
`
// todo headers
const TodoTitle = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 32px;
`
const TodoTitleNails = styled.div`
  margin: 10px;
  width:20px;
  height:20px;
  border: #dbe3e5 solid;
  border-radius: 100%;
  background-color: #dbe3e5;
  box-shadow: 1.8px 2.4px 5px 0 rgba(0, 0, 0, 0.3);
`
const TodoInput = styled.input`
  display: block;
  margin-top: 30px;
  margin-bottom: 35px;
  align-self: center;
  text-align: center;
  padding: 10px 10px;
  width: 100%;
  opacity: 100%;
  background: #f6f4e6;
  color: #776d8a;
  border: none;
  border-radius: 5px;
  transition: all 0.3s;
  outline: none;
  font-size: 20px;
  :hover {
    background: #e8ded2;
    opacity: 50%;
  }
  :focus {
    opacity: 100%;
    color: #776d8a;
    background: #e8ded2;
  }
  ::placeholder {
    color: #776d8a;
    opacity: 50%;
  }
`

// todo filters

const TodoFilters = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 600px;
  margin: 20px 0;
`

// todo items
const TodoItems = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  margin: 20px 0;
`
const TodoItem = styled.div`
  position: relative;
  padding: 12px 24px;
  font-size: 20px;
  transition: all 0.2s;
  margin-top: 5px;
  margin-botton: 10px;
  :hover {
    background-color: #d9c6a5;
  }
`


function App() {
  return (
    <TodoWrapper>
      <Todolist>
        <TodoTitle>
          <TodoTitleNails />
          Todolist
          <TodoTitleNails />
        </TodoTitle>
        <TodoInput placeholder="add something todo here!" />
        <ButtonGroup>
          <Button variant="outline-secondary">All</Button>
          <Button variant="outline-secondary">Done</Button>
          <Button variant="outline-secondary">Undone</Button>
        </ButtonGroup>
        <TodoItems>
          <TodoItem></TodoItem>
        </TodoItems>



      </Todolist>
    </TodoWrapper>
  );
}

export default App;
