import { todos } from './useTodo'

export default function updateFilter(e) {
  const buttonPress = e.target.value
  switch (buttonPress) {
    case 'Done':
      setTodos(
        todos.map((todo)=> {
          if(todo.isDone){
            return{
              ...todo,
              isDone: true,
            }
          }
          return  {
            ...todo,
              isDone: false,
          }
        })
      )
      break;
      case 'Undone':
        setTodos(
          todos.map((todo)=> {
            if(!todo.isDone){
              return{
                ...todo,
                isDone: false,
              }
            }
            return  {
              ...todo,
                isDone: true,
            }
          })
        )
        break;
  }

}
