import TodoUI from './TodoUI'
import styled from 'styled-components';
import { Button, ButtonGroup } from 'react-bootstrap';


//todo wrapper
const TodoWrapper = styled.div`
  display: flex;
  justify-content: space-around;
  max-width: 645px;
  background: #f6f4e6;
  margin: 10% auto;
  box-shadow: 1.8px 2.4px 5px 0 rgba(0, 0, 0, 0.3);
  padding: 10px 10px;
  color: #776d8a;
  border-radius: 3%;
`
const Todolist = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 600px;
`
// todo headers
const TodoTitle = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: 32px;
`
const TodoTitleNails = styled.div`
  margin: 10px;
  width:20px;
  height:20px;
  border: #dbe3e5 solid;
  border-radius: 100%;
  background-color: #dbe3e5;
  box-shadow: 1.8px 2.4px 5px 0 rgba(0, 0, 0, 0.3);
`
const TodoInput = styled.input`
  display: block;
  margin-top: 30px;
  margin-bottom: 35px;
  align-self: center;
  text-align: center;
  padding: 10px 10px;
  width: 100%;
  opacity: 100%;
  background: #f6f4e6;
  color: #776d8a;
  border: none;
  border-radius: 5px;
  transition: all 0.3s;
  outline: none;
  font-size: 20px;
  :hover {
    background: #e8ded2;
    opacity: 50%;
  }
  :focus {
    opacity: 100%;
    color: #776d8a;
    background: #e8ded2;
  }
  ::placeholder {
    color: #776d8a;
    opacity: 50%;
  }
`

// todo filters

// const TodoFilters = styled.div`
//   display: flex;
//   flex-direction: column;
//   justify-content: center;
//   width: 600px;
//   margin: 20px 0;
// `

// todo items
const TodoItems = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  margin: 20px 0;
`
const TodoItem = styled.div`
  display: flex;
  flex-direction: row;
  position: relative;
  padding: 12px 24px;
  font-size: 20px;
  transition: all 0.2s;
  padding:5px;
  margin-top: 5px;
  margin-botton: 10px;
  :hover {
    background-color: #d9c6a5;
  }
  + .list_item  {
    margin-top: 10px;
  }
`
const TodoItemCheckbox = styled.input`
  position: relative;
  display: inline-block;
  background: #f6f4e6;
  color: #776d8a 2px;
  width: 15px;
  height: 15px;
  transition: all 0.2s;
  :hover {
    cursor: pointer;
  }
`
const TodoItemText = styled.label`
  display: inline-block;
  margin: 10px 0;
  margin-left: 20px;
  font-size: 16px;
`
const TodoItemDelete = styled.button`
  position: absolute;
  top: 8px;
  right: 30px;
  font-size: 16px;
  border-style: none;
  background-color:rgba(0, 0, 0, 0);
  ::after { 
    font-size: 15px;
    content: "X";
    color: #776d8a;
    text-align: center;
  }
  :hover { 
    opacity: 50%;
    cursor: pointer;
  }
`

function App() {
  return (
    <TodoWrapper>
      <Todolist>
        <TodoTitle>
          <TodoTitleNails />
          Todolist
          <TodoTitleNails />
        </TodoTitle>
        <TodoInput placeholder="add something todo here!" />
        <ButtonGroup>
          <Button variant="outline-secondary">All</Button>
          <Button variant="outline-secondary">Done</Button>
          <Button variant="outline-secondary">Undone</Button>
        </ButtonGroup>
        <TodoItems>
          <TodoItem>
            <TodoItemCheckbox type="checkbox" checked></TodoItemCheckbox>
            <TodoItemText></TodoItemText>
            <TodoItemDelete></TodoItemDelete>
          </TodoItem>
          <TodoItem>
            <TodoItemCheckbox type="checkbox"></TodoItemCheckbox>
            <TodoItemText>todotest</TodoItemText>
            <TodoItemDelete></TodoItemDelete>
          </TodoItem>
        </TodoItems>



      </Todolist>
    </TodoWrapper>
  );
}

export default App;
