import { useState } from "react";
import useTodo from './useTodo';

export default function useInput() {
  const [ value, setValue ] = useState("")
  const { id, todos, setTodos } = useTodo()
  const [ checked, setChecked] = useState((id) => {
    todos.map((todo) => {
      if (todo.id !== id) return todo;
      return setChecked(todo.isDone);
    })
  })

  const handleInputChange = (e) => {
    setValue(e.target.value)
  }

  const handleEnterKeyPress = (e) => {
    if(e.key ==='Enter' && value){
      setTodos([
        {
          id: id.current,
          content: value,
          isDone: false
        },
        ...todos,
      ]);
      e.target.value = ''
      // setValue does not work , why?
      id.current++
    }
  }

  const handleDeleteButton = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id ))
  }

  const handleCheckBox = (checked, id) => {
    setTodos(
      todos.map((e) => {
        if (e.todo.id === id) return e.todo.isDone = checked;
      })
    );
  };


  return ({
      value, 
      setValue,
      id, 
      todos,
      checked,
      setChecked,
      setTodos,
      handleInputChange,
      handleEnterKeyPress,
      handleDeleteButton,
      handleCheckBox
    }
  )
}
